package bd.edu.seu;import java.util.Scanner;public class Main {    public static Scanner input = new Scanner(System.in);    public static int q=1;    public static void main(String[] args) {        double[] v1,v2;        v1=inputValue();        v2=inputValue();        printValue(vectorCalculate(v1,v2));    }    private static double[] vectorCalculate(double[] v1, double[] v2) {        double[] arr=new double[v1.length];        for(int i=0;i<v1.length;i++){            arr[i]=v2[i]-v1[i];        }        return arr;    }    private static void printValue(double[] v1) {        for(int i=0;i<v1.length;i++){            System.out.print(v1[i]+" ");        }        System.out.println();    }    private static double[] inputValue() {        System.out.println("Enter point "+q++);        double[] arr=new double[3];        for(int i=0;i<3;i++){            arr[i] = input.nextDouble();        }        return arr;    }}