package bd.edu.seu;import java.util.Scanner;public class Main {    public static Scanner input = new Scanner(System.in);    public static void main(String[] args) {        double[] v1,v2,v3,a,b,surfaceNormal;        System.out.println("Enter vector 1");        v1=inputValue(3);        System.out.println("Enter vector 2");        v2=inputValue(3);        System.out.println("Enter vector 3");        v3=inputValue(3);        a = vectorCalculate(v1,v2);        b = vectorCalculate(v1,v3);        surfaceNormal = calculateSurfaceNormal(a,b);        System.out.println("Vector a");        printValue(a);        System.out.println("vector b");        printValue(b);        System.out.println("Surface Normal");        printValue(surfaceNormal);    }    private static double[] calculateSurfaceNormal(double[] a, double[] b) {        double[] surfaceNormal = new double[a.length];        surfaceNormal[0] = (a[1]*b[2])-(a[2]*b[1]);        surfaceNormal[1] = -((a[0]*b[2])-(a[2]*b[0]));        surfaceNormal[2] = (a[0]*b[1])-(a[1]*b[0]);        return surfaceNormal;    }    private static double[] vectorCalculate(double[] v1, double[] v2) {        double[] arr=new double[v1.length];        for(int i=0;i<v1.length;i++){            arr[i]=v2[i]-v1[i];        }        return arr;    }    private static void printValue(double[] v1) {        for(int i=0;i<v1.length;i++){            System.out.print(v1[i]+" ");        }        System.out.println();    }    private static double[] inputValue(int n) {        double[] arr = new double[n];        for(int i=0;i<n;i++){            arr[i]=input.nextDouble();        }        return arr;    }}