package bd.edu.seu;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        double Is,Pd;        double[] l,n;        System.out.println("Enter the value of light source(Is): ");        Is = input.nextDouble();        System.out.println("Enter the value of defuse reflection coeefficient(Pd): ");        Pd = input.nextDouble();        System.out.println("Enter the vector l");        l = inputValue();        System.out.println("Enter the value of n");        n = inputValue();        System.out.println("\n\n");        double Id = Is*Pd*Math.max(res(l,n),0);        System.out.println("Defuse Reflection is : "+Id);    }    private static double res(double[] l, double[] n) {        double res=0,lDotn=0,magl=0,magn=0;        for(int i=0;i<l.length;i++){            lDotn+=l[i]*n[i];        }        magl = magnatude(l);        magn = magnatude(n);        System.out.println("l dot n: "+lDotn);        res = (lDotn/(magl*magn));        return res;    }    private static double magnatude(double[] l) {        double sum=0;        for(int i=0;i<l.length;i++) {            sum += Math.pow(l[i], 2);        }        System.out.println("Magnitude: "+sum);        double res = Math.sqrt(sum);        return res;    }    private static double[] inputValue() {        Scanner input = new Scanner(System.in);        double[] arr = new double[3];        for(int i=0;i<3;i++){            arr[i] = input.nextDouble();        }        return arr;    }}