package bd.edu.seu;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        System.out.println("Enter the number size: ");        int n = input.nextInt();        System.out.println("Enter "+n+" numbers for calculate: ");        double[] numbers = new double[n];        for(int i=0;i<n;i++){            numbers[i]=input.nextDouble();        }        double sum = 0;        for(int i=0;i<n;i++){            sum+=numbers[i];        }        System.out.println("Mean : "+(sum/n));    }}