package bd.edu.seu;public class Main {    public static void main(String[] args) {        int [] arr = {10,7,8,9,1,5};        sort_function(arr,0,arr.length-1);        for(int i=0;i<arr.length;i++){            System.out.print(arr[i]+" ");        }    }    private static void sort_function(int[] arr, int low, int high) {        if(low<high){            int mid = partition(arr,low,high);            sort_function(arr,low,mid-1);            sort_function(arr,mid+1,high);        }    }    private static int partition(int[] arr, int low, int high) {        int pivot = arr[high];        int i=(low-1);        for(int j=low;j<high;j++){            if(arr[j]<pivot){                i++;                int temp = arr[j];                arr[j]=arr[i];                arr[i]=temp;            }        }        int temp = arr[i+1];        arr[i+1] = arr[high];        arr[high] = temp;        return i+1;    }}