package bd.edu.seu;public class Main {    public static void main(String[] args) {        int [] arr = {10,7,8,9,1,5};        quick_sort(arr,0,arr.length-1);        for(int i=0;i<arr.length;i++){            System.out.print(arr[i]+" ");        }    }    private static void quick_sort(int[] arr,int low,int high) {        if(low<high){            int pivot = partition(arr,low,high);            quick_sort(arr,low,pivot-1);            quick_sort(arr,pivot+1,high);        }    }    private static int partition(int[] arr, int low, int high) {        int pivot = arr[low];        int leftWall = low;        for(int i=low+1;i<=high;i++){            if(arr[i]<pivot){                leftWall++;                int temp = arr[i];                arr[i] = arr[leftWall];                arr[leftWall] = temp;            }        }        int temp = arr[low];        arr[low] = arr[leftWall];        arr[leftWall] = temp;        return leftWall;    }}