package bd.edu.seu;public class Main {    static boolean isSubsetSum(int[] set, int n, int sum)    {        boolean[][] subset = new boolean[n+1][sum+1];        for (int i = 1; i <= sum; i++)            subset[0][i] = false;        for (int i = 0; i <= n; i++)            subset[i][0] = true;        for (int i = 1; i <= n; i++)        {            for (int j = 1; j <= sum; j++)            {                subset[i][j] = subset[i-1][j];                if (j >= set[i-1]) {                    subset[i][j] = subset[i][j] || subset[i-1][j - set[i-1]];                }            }        }        for(int i=0;i<=n;i++){            for(int j=0;j<=sum;j++){                System.out.print(subset[i][j]+" ");            }            System.out.println();        }        return subset[n][sum];    }    public static void main (String[] args)    {        int[] set = {1,2,3,5,7};        int sum = 8;        int n = set.length;        if (isSubsetSum(set, n, sum))            System.out.println("Found a subset with given sum");        else            System.out.println("No subset with given sum");    }}