package bd.edu.seu;public class Main {    public static void main(String[] args) {        int arr[]={3,4,55,-34,78,0,-343};        bubble_sor(arr);        for(int value:arr){            System.out.print(value+" ");        }    }    private static void bubble_sor(int[] arr) {        for(int i=0;i<arr.length-1;i++){            boolean swap = false;            for(int j=0;j<arr.length-1;j++){                if(arr[j]>arr[j+1]){                    int temp = arr[j];                    arr[j] = arr[j+1];                    arr[j+1] = temp;                    swap = true;                }            }            if(!swap){                break;            }        }    }}