package bd.edu.seu;public class Main {    public static void main(String[] args) {        int[] arr={3,-2323,34,45,1,-45,645,0,-4545};        insertion_sort(arr);        for(int i=0;i<arr.length;i++){            System.out.print(arr[i]+" ");        }    }    private static void insertion_sort(int[] arr) {        int holePosition;        int valueToInsert;        for(int i=1;i<arr.length;i++){            valueToInsert = arr[i];            holePosition = i;            while (holePosition>0 && arr[holePosition-1]>valueToInsert){                arr[holePosition] = arr[holePosition-1];                holePosition = holePosition-1;            }            arr[holePosition] = valueToInsert;        }    }}