package bd.edu.seu;public class Main {    public static void main(String[] args) {        int arr[]={3,4,55,-34,78,0,-343};        selection_sort(arr);        for(int value:arr){            System.out.print(value+" ");        }    }    private static void selection_sort(int[] arr) {        int min;        for(int i=0;i<arr.length;i++){            min = i;            for(int j=i+1;j<arr.length;j++){                if(arr[j]<arr[min]){                    min = j;                }            }            if(min!=i){                int temp = arr[min];                arr[min] = arr[i];                arr[i] = temp;            }        }    }}